package com.juank.utp.finimpact.controller;

import com.juank.utp.finimpact.model.Usuario;
import com.juank.utp.finimpact.repository.UsuarioRepository;
import com.juank.utp.finimpact.utils.PasswordUtils;
import javafx.fxml.FXML;
import javafx.scene.control.*;

/**
 * Controlador para la vista de perfil del usuario
 */
public class PerfilController {

    // Componentes de información personal
    @FXML private Label lblNombreCompleto;
    @FXML private Label lblUsuario;
    @FXML private Label lblEmail;
    @FXML private Label lblTipo;

    // Componentes de cambio de contraseña
    @FXML private PasswordField txtPasswordActual;
    @FXML private PasswordField txtPasswordNueva;
    @FXML private PasswordField txtPasswordConfirmar;

    private Usuario usuario;
    private UsuarioRepository usuarioRepository;

    public void initialize() {
        usuarioRepository = new UsuarioRepository();
    }

    /**
     * Establece el usuario cuyo perfil se va a mostrar
     */
    public void setUsuario(Usuario usuario) {
        this.usuario = usuario;
        cargarDatosUsuario();
    }

    /**
     * Carga los datos del usuario en la interfaz
     */
    private void cargarDatosUsuario() {
        if (usuario != null) {
            lblNombreCompleto.setText(usuario.getNombreCompleto());
            lblUsuario.setText(usuario.getUsuario());
            lblEmail.setText(usuario.getEmail());
            lblTipo.setText(getTipoDisplayName(usuario.getRol()));
        }
    }

    /**
     * Maneja el evento de cambiar contraseña
     */
    @FXML
    private void cambiarPassword() {
        if (usuario == null) {
            mostrarError("Error", "No se ha cargado la información del usuario.");
            return;
        }

        // Validaciones
        String passwordActual = txtPasswordActual.getText().trim();
        String passwordNueva = txtPasswordNueva.getText().trim();
        String passwordConfirmar = txtPasswordConfirmar.getText().trim();

        if (passwordActual.isEmpty()) {
            mostrarError("Error", "Debe ingresar su contraseña actual.");
            return;
        }

        if (passwordNueva.isEmpty()) {
            mostrarError("Error", "Debe ingresar la nueva contraseña.");
            return;
        }

        if (passwordNueva.length() < 6) {
            mostrarError("Error", "La nueva contraseña debe tener al menos 6 caracteres.");
            return;
        }

        if (!passwordNueva.equals(passwordConfirmar)) {
            mostrarError("Error", "La confirmación de contraseña no coincide.");
            return;
        }

        // Verificar contraseña actual
        if (!PasswordUtils.verifyPassword(passwordActual, usuario.getPassword())) {
            mostrarError("Error", "La contraseña actual es incorrecta.");
            return;
        }

        // Cambiar contraseña
        try {
            String nuevaPasswordHash = PasswordUtils.hashPassword(passwordNueva);
            usuario.setPassword(nuevaPasswordHash);
            usuarioRepository.update(usuario);

            mostrarInformacion("Éxito", "Contraseña cambiada correctamente.");
            limpiarCampos();

        } catch (Exception e) {
            mostrarError("Error", "Error al cambiar la contraseña: " + e.getMessage());
        }
    }

    /**
     * Limpia los campos de contraseña
     */
    @FXML
    private void limpiarCampos() {
        txtPasswordActual.clear();
        txtPasswordNueva.clear();
        txtPasswordConfirmar.clear();
    }

    /**
     * Convierte el tipo técnico a un nombre más amigable
     */
    private String getTipoDisplayName(String tipo) {
        switch (tipo.toLowerCase()) {
            case "admin":
                return "Administrador";
            case "analista":
                return "Analista";
            case "viewer":
                return "Visualizador";
            default:
                return tipo;
        }
    }

    /**
     * Muestra un mensaje de error
     */
    private void mostrarError(String titulo, String mensaje) {
        Alert alert = new Alert(Alert.AlertType.ERROR);
        alert.setTitle(titulo);
        alert.setHeaderText(null);
        alert.setContentText(mensaje);
        alert.showAndWait();
    }

    /**
     * Muestra un mensaje de información
     */
    private void mostrarInformacion(String titulo, String mensaje) {
        Alert alert = new Alert(Alert.AlertType.INFORMATION);
        alert.setTitle(titulo);
        alert.setHeaderText(null);
        alert.setContentText(mensaje);
        alert.showAndWait();
    }
}

package com.juank.utp.finimpact.controller;

import com.juank.utp.finimpact.model.Usuario;
import com.juank.utp.finimpact.repository.UsuarioRepository;
import com.juank.utp.finimpact.utils.PasswordUtils;
import javafx.fxml.FXML;
import javafx.scene.control.*;

/**
 * Controlador para la vista de perfil del usuario
 */
public class PerfilController {

    // Componentes de información personal
    @FXML private Label lblNombreCompleto;
    @FXML private Label lblUsuario;
    @FXML private Label lblEmail;
    @FXML private Label lblTipo;

    // Componentes de cambio de contraseña
    @FXML private PasswordField txtPasswordActual;
    @FXML private PasswordField txtPasswordNueva;
    @FXML private PasswordField txtPasswordConfirmar;

    private Usuario usuario;
    private UsuarioRepository usuarioRepository;

    public void initialize() {
        usuarioRepository = new UsuarioRepository();
    }

    /**
     * Establece el usuario cuyo perfil se va a mostrar
     */
    public void setUsuario(Usuario usuario) {
        this.usuario = usuario;
        cargarDatosUsuario();
    }

    /**
     * Carga los datos del usuario en la interfaz
     */
    private void cargarDatosUsuario() {
        if (usuario != null) {
            lblNombreCompleto.setText(usuario.getNombreCompleto());
            lblUsuario.setText(usuario.getEmail()); // Usar email como nombre de usuario
            lblEmail.setText(usuario.getEmail());
            lblTipo.setText(getTipoDisplayName(usuario.getRol())); // Usar getRol() en lugar de getTipo()
        }
    }

    /**
     * Maneja el evento de cambiar contraseña
     */
    @FXML
    private void cambiarPassword() {
        if (usuario == null) {
            mostrarError("Error", "No se ha cargado la información del usuario.");
            return;
        }

        // Validaciones
        String passwordActual = txtPasswordActual.getText().trim();
        String passwordNueva = txtPasswordNueva.getText().trim();
        String passwordConfirmar = txtPasswordConfirmar.getText().trim();

        if (passwordActual.isEmpty()) {
            mostrarError("Error", "Debe ingresar su contraseña actual.");
            return;
        }

        if (passwordNueva.isEmpty()) {
            mostrarError("Error", "Debe ingresar la nueva contraseña.");
            return;
        }

        if (passwordNueva.length() < 6) {
            mostrarError("Error", "La nueva contraseña debe tener al menos 6 caracteres.");
            return;
        }

        if (!passwordNueva.equals(passwordConfirmar)) {
            mostrarError("Error", "La confirmación de contraseña no coincide.");
            return;
        }

        // Verificar contraseña actual
        if (!PasswordUtils.verifyPassword(passwordActual, usuario.getPassword())) {
            mostrarError("Error", "La contraseña actual es incorrecta.");
            return;
        }

        // Cambiar contraseña
        try {
            String nuevaPasswordHash = PasswordUtils.hashPassword(passwordNueva);
            usuario.setPassword(nuevaPasswordHash);
            usuarioRepository.update(usuario);

            mostrarInformacion("Éxito", "Contraseña cambiada correctamente.");
            limpiarCampos();

        } catch (Exception e) {
            mostrarError("Error", "Error al cambiar la contraseña: " + e.getMessage());
        }
    }

    /**
     * Limpia los campos de contraseña
     */
    @FXML
    private void limpiarCampos() {
        txtPasswordActual.clear();
        txtPasswordNueva.clear();
        txtPasswordConfirmar.clear();
    }

    /**
     * Convierte el rol técnico a un nombre más amigable
     */
    private String getTipoDisplayName(String rol) {
        switch (rol.toLowerCase()) {
            case "admin":
                return "Administrador";
            case "analista":
                return "Analista";
            case "viewer":
                return "Visualizador";
            default:
                return rol;
        }
    }

    /**
     * Muestra un mensaje de error
     */
    private void mostrarError(String titulo, String mensaje) {
        Alert alert = new Alert(Alert.AlertType.ERROR);
        alert.setTitle(titulo);
        alert.setHeaderText(null);
        alert.setContentText(mensaje);
        alert.showAndWait();
    }

    /**
     * Muestra un mensaje de información
     */
    private void mostrarInformacion(String titulo, String mensaje) {
        Alert alert = new Alert(Alert.AlertType.INFORMATION);
        alert.setTitle(titulo);
        alert.setHeaderText(null);
        alert.setContentText(mensaje);
        alert.showAndWait();
    }
}

package com.juank.utp.finimpact.controller;

import com.juank.utp.finimpact.model.Usuario;
import com.juank.utp.finimpact.repository.UsuarioRepository;
import com.juank.utp.finimpact.utils.PasswordUtils;
import javafx.beans.property.SimpleStringProperty;
import javafx.collections.FXCollections;
import javafx.collections.ObservableList;
import javafx.fxml.FXML;
import javafx.fxml.Initializable;
import javafx.geometry.Insets;
import javafx.scene.Scene;
import javafx.scene.control.*;
import javafx.scene.layout.GridPane;
import javafx.scene.layout.HBox;
import javafx.stage.Modality;
import javafx.stage.Stage;

import java.net.URL;
import java.util.List;
import java.util.Optional;
import java.util.ResourceBundle;

/**
 * Controlador para la gestión de usuarios (solo admin)
 */
public class UsuarioController implements Initializable {

    // Componentes de la vista
    @FXML private ComboBox<String> cbTipo;
    @FXML private TextField txtFiltroNombre;
    @FXML private TableView<Usuario> tableUsuarios;
    @FXML private TableColumn<Usuario, String> colId;
    @FXML private TableColumn<Usuario, String> colNombreCompleto;
    @FXML private TableColumn<Usuario, String> colUsuario;
    @FXML private TableColumn<Usuario, String> colEmail;
    @FXML private TableColumn<Usuario, String> colTipo;
    @FXML private TableColumn<Usuario, Void> colAcciones;

    // Repositorio
    private UsuarioRepository usuarioRepository;

    // Lista de datos
    private final ObservableList<Usuario> usuariosList = FXCollections.observableArrayList();
    private final ObservableList<Usuario> usuariosFiltradosList = FXCollections.observableArrayList();

    @Override
    public void initialize(URL location, ResourceBundle resources) {
        usuarioRepository = new UsuarioRepository();

        configurarTabla();
        cargarUsuarios();
        configurarFiltros();
        configurarFiltrosAutomaticos();
    }

    private void configurarTabla() {
        // Configurar columnas
        colId.setCellValueFactory(cellData -> new SimpleStringProperty(String.valueOf(cellData.getValue().getId())));
        colNombreCompleto.setCellValueFactory(cellData -> new SimpleStringProperty(cellData.getValue().getNombreCompleto()));
        colUsuario.setCellValueFactory(cellData -> new SimpleStringProperty(cellData.getValue().getEmail())); // Usando email como usuario
        colEmail.setCellValueFactory(cellData -> new SimpleStringProperty(cellData.getValue().getEmail()));
        colTipo.setCellValueFactory(cellData -> new SimpleStringProperty(getTipoDisplayName(cellData.getValue().getRol())));

        // Configurar columna de acciones
        colAcciones.setCellFactory(param -> new TableCell<>() {
            private final Button btnEditar = new Button("Editar");
            private final Button btnEliminar = new Button("Eliminar");
            private final HBox pane = new HBox(8);

            {
                // Hacer los botones más grandes
                btnEditar.setPrefWidth(80);
                btnEditar.setPrefHeight(35);
                btnEliminar.setPrefWidth(80);
                btnEliminar.setPrefHeight(35);

                btnEditar.setStyle("-fx-background-color: #4CAF50; -fx-text-fill: white; -fx-background-radius: 5; -fx-font-size: 12px; -fx-font-weight: bold;");
                btnEliminar.setStyle("-fx-background-color: #f44336; -fx-text-fill: white; -fx-background-radius: 5; -fx-font-size: 12px; -fx-font-weight: bold;");

                pane.getChildren().addAll(btnEditar, btnEliminar);
                pane.setAlignment(javafx.geometry.Pos.CENTER);

                btnEditar.setOnAction(event -> {
                    Usuario usuario = getTableView().getItems().get(getIndex());
                    editarUsuario(usuario);
                });

                btnEliminar.setOnAction(event -> {
                    Usuario usuario = getTableView().getItems().get(getIndex());
                    eliminarUsuario(usuario);
                });
            }

            @Override
            protected void updateItem(Void item, boolean empty) {
                super.updateItem(item, empty);
                setGraphic(empty ? null : pane);
            }
        });

        tableUsuarios.setItems(usuariosFiltradosList);
    }

    private void configurarFiltros() {
        cbTipo.setValue("Todos");
    }

    private void configurarFiltrosAutomaticos() {
        // Configurar filtros automáticos
        cbTipo.valueProperty().addListener((observable, oldValue, newValue) -> aplicarFiltros());
        txtFiltroNombre.textProperty().addListener((observable, oldValue, newValue) -> aplicarFiltros());
    }

    @FXML
    private void mostrarFormularioUsuario() {
        mostrarFormulario(null);
    }

    @FXML
    private void aplicarFiltros() {
        String tipoFiltro = cbTipo.getValue();
        String nombreFiltro = txtFiltroNombre.getText().toLowerCase();

        usuariosFiltradosList.clear();

        for (Usuario usuario : usuariosList) {
            boolean cumpleTipo = "Todos".equals(tipoFiltro) || usuario.getTipo().equals(tipoFiltro);
            boolean cumpleNombre = nombreFiltro.isEmpty() ||
                    usuario.getNombreCompleto().toLowerCase().contains(nombreFiltro) ||
                    usuario.getUsuario().toLowerCase().contains(nombreFiltro);

            if (cumpleTipo && cumpleNombre) {
                usuariosFiltradosList.add(usuario);
            }
        }
    }

    @FXML
    private void limpiarFiltros() {
        cbTipo.setValue("Todos");
        txtFiltroNombre.clear();
        usuariosFiltradosList.setAll(usuariosList);
    }

    private void cargarUsuarios() {
        try {
            List<Usuario> usuarios = usuarioRepository.findAll();
            usuariosList.setAll(usuarios);
            usuariosFiltradosList.setAll(usuarios);
        } catch (Exception e) {
            mostrarError("Error al cargar usuarios", e.getMessage());
        }
    }

    private void editarUsuario(Usuario usuario) {
        mostrarFormulario(usuario);
    }

    private void eliminarUsuario(Usuario usuario) {
        Alert alert = new Alert(Alert.AlertType.CONFIRMATION);
        alert.setTitle("Confirmar eliminación");
        alert.setHeaderText("¿Está seguro de eliminar este usuario?");
        alert.setContentText("Esta acción no se puede deshacer.");

        Optional<ButtonType> result = alert.showAndWait();
        if (result.isPresent() && result.get() == ButtonType.OK) {
            try {
                usuarioRepository.delete(usuario.getId());
                cargarUsuarios();
                mostrarInformacion("Éxito", "Usuario eliminado correctamente.");
            } catch (Exception e) {
                mostrarError("Error al eliminar usuario", e.getMessage());
            }
        }
    }

