package com.juank.utp.finimpact.model;

import java.time.LocalDate;
import java.time.LocalDateTime;

/**
 * Modelo que representa una iniciativa empresarial
 */
public class Iniciativa {
    private int idIniciativa;
    private String nombre;
    private String descripcion;
    private LocalDate fechaInicio;
    private LocalDate fechaFin;
    private String tipo;
    private String estado; // planeado, en curso, finalizado, cancelado
    private String riesgo; // alto, medio, bajo
    private int idOwner;
    private LocalDateTime fechaRegistro;

    // Constructor vacío
    public Iniciativa() {}

    // Constructor completo
    public Iniciativa(int idIniciativa, String nombre, String descripcion, LocalDate fechaInicio,
                     LocalDate fechaFin, String tipo, String estado, String riesgo, int idOwner,
                     LocalDateTime fechaRegistro) {
        this.idIniciativa = idIniciativa;
        this.nombre = nombre;
        this.descripcion = descripcion;
        this.fechaInicio = fechaInicio;
        this.fechaFin = fechaFin;
        this.tipo = tipo;
        this.estado = estado;
        this.riesgo = riesgo;
        this.idOwner = idOwner;
        this.fechaRegistro = fechaRegistro;
    }

    // Constructor sin ID (para inserción)
    public Iniciativa(String nombre, String descripcion, LocalDate fechaInicio, LocalDate fechaFin,
                     String tipo, String estado, String riesgo, int idOwner) {
        this.nombre = nombre;
        this.descripcion = descripcion;
        this.fechaInicio = fechaInicio;
        this.fechaFin = fechaFin;
        this.tipo = tipo;
        this.estado = estado;
        this.riesgo = riesgo;
        this.idOwner = idOwner;
    }

    // Getters y Setters
    public int getIdIniciativa() {
        return idIniciativa;
    }

    public void setIdIniciativa(int idIniciativa) {
        this.idIniciativa = idIniciativa;
    }

    public String getNombre() {
        return nombre;
    }

    public void setNombre(String nombre) {
        this.nombre = nombre;
    }

    public String getDescripcion() {
        return descripcion;
    }

    public void setDescripcion(String descripcion) {
        this.descripcion = descripcion;
    }

    public LocalDate getFechaInicio() {
        return fechaInicio;
    }

    public void setFechaInicio(LocalDate fechaInicio) {
        this.fechaInicio = fechaInicio;
    }

    public LocalDate getFechaFin() {
        return fechaFin;
    }

    public void setFechaFin(LocalDate fechaFin) {
        this.fechaFin = fechaFin;
    }

    public String getTipo() {
        return tipo;
    }

    public void setTipo(String tipo) {
        this.tipo = tipo;
    }

    public String getEstado() {
        return estado;
    }

    public void setEstado(String estado) {
        this.estado = estado;
    }

    public String getRiesgo() {
        return riesgo;
    }

    public void setRiesgo(String riesgo) {
        this.riesgo = riesgo;
    }

    public int getIdOwner() {
        return idOwner;
    }

    public void setIdOwner(int idOwner) {
        this.idOwner = idOwner;
    }

    public LocalDateTime getFechaRegistro() {
        return fechaRegistro;
    }

    public void setFechaRegistro(LocalDateTime fechaRegistro) {
        this.fechaRegistro = fechaRegistro;
    }

    @Override
    public String toString() {
        return "Iniciativa{" +
                "idIniciativa=" + idIniciativa +
                ", nombre='" + nombre + '\'' +
                ", descripcion='" + descripcion + '\'' +
                ", fechaInicio=" + fechaInicio +
                ", fechaFin=" + fechaFin +
                ", tipo='" + tipo + '\'' +
                ", estado='" + estado + '\'' +
                ", riesgo='" + riesgo + '\'' +
                ", idOwner=" + idOwner +
                ", fechaRegistro=" + fechaRegistro +
                '}';
    }
}

package com.juank.utp.finimpact.model;

import java.time.LocalDate;
import java.time.LocalDateTime;

/**
 * Modelo que representa una iniciativa empresarial
 */
public class Iniciativa {
    private int idIniciativa;
    private String nombre;
    private String descripcion;
    private LocalDate fechaInicio;
    private LocalDate fechaFin;
    private String tipo;
    private String estado; // planeado, en curso, finalizado, cancelado
    private String riesgo; // alto, medio, bajo
    private int idOwner;
    private LocalDateTime fechaRegistro;

    // Constructor vacío
    public Iniciativa() {}

    // Constructor completo
    public Iniciativa(int idIniciativa, String nombre, String descripcion, LocalDate fechaInicio,
                     LocalDate fechaFin, String tipo, String estado, String riesgo, int idOwner,
                     LocalDateTime fechaRegistro) {
        this.idIniciativa = idIniciativa;
        this.nombre = nombre;
        this.descripcion = descripcion;
        this.fechaInicio = fechaInicio;
        this.fechaFin = fechaFin;
        this.tipo = tipo;
        this.estado = estado;
        this.riesgo = riesgo;
        this.idOwner = idOwner;
        this.fechaRegistro = fechaRegistro;
    }

    // Constructor sin ID (para inserción)
    public Iniciativa(String nombre, String descripcion, LocalDate fechaInicio, LocalDate fechaFin,
                     String tipo, String estado, String riesgo, int idOwner) {
        this.nombre = nombre;
        this.descripcion = descripcion;
        this.fechaInicio = fechaInicio;
        this.fechaFin = fechaFin;
        this.tipo = tipo;
        this.estado = estado;
        this.riesgo = riesgo;
        this.idOwner = idOwner;
    }

    // Getters y Setters
    public int getId() {
        return idIniciativa;
    }

    public void setId(int id) {
        this.idIniciativa = id;
    }

    public int getIdIniciativa() {
        return idIniciativa;
    }

    public void setIdIniciativa(int idIniciativa) {
        this.idIniciativa = idIniciativa;
    }

    public String getNombre() {
        return nombre;
    }

    public void setNombre(String nombre) {
        this.nombre = nombre;
    }

    public String getDescripcion() {
        return descripcion;
    }

    public void setDescripcion(String descripcion) {
        this.descripcion = descripcion;
    }

    public LocalDate getFechaInicio() {
        return fechaInicio;
    }

    public void setFechaInicio(LocalDate fechaInicio) {
        this.fechaInicio = fechaInicio;
    }

    public LocalDate getFechaFin() {
        return fechaFin;
    }

    public void setFechaFin(LocalDate fechaFin) {
        this.fechaFin = fechaFin;
    }

    public String getTipo() {
        return tipo;
    }

    public void setTipo(String tipo) {
        this.tipo = tipo;
    }

    public String getEstado() {
        return estado;
    }

    public void setEstado(String estado) {
        this.estado = estado;
    }

    public String getRiesgo() {
        return riesgo;
    }

    public void setRiesgo(String riesgo) {
        this.riesgo = riesgo;
    }

    public int getUsuarioId() {
        return idOwner;
    }

    public void setUsuarioId(int usuarioId) {
        this.idOwner = usuarioId;
    }

    public int getIdOwner() {
        return idOwner;
    }

    public void setIdOwner(int idOwner) {
        this.idOwner = idOwner;
    }

    public LocalDateTime getFechaRegistro() {
        return fechaRegistro;
    }

    public void setFechaRegistro(LocalDateTime fechaRegistro) {
        this.fechaRegistro = fechaRegistro;
    }

    @Override
    public String toString() {
        return nombre;
    }
}

package com.juank.utp.finimpact.model;

/**
 * Modelo que representa un usuario del sistema
 */
public class Usuario {
    private int idUsuario;
    private String nombreCompleto;
    private String email;
    private String password; // Hash de la contraseña
    private String rol; // admin, analista, viewer
    private boolean estado; // true = activo, false = inactivo

    // Constructor vacío
    public Usuario() {}

    // Constructor completo
    public Usuario(int idUsuario, String nombreCompleto, String email, String password, String rol, boolean estado) {
        this.idUsuario = idUsuario;
        this.nombreCompleto = nombreCompleto;
        this.email = email;
        this.password = password;
        this.rol = rol;
        this.estado = estado;
    }

    // Constructor sin ID (para inserción)
    public Usuario(String nombreCompleto, String email, String password, String rol, boolean estado) {
        this.nombreCompleto = nombreCompleto;
        this.email = email;
        this.password = password;
        this.rol = rol;
        this.estado = estado;
    }

    // Getters y Setters
    public int getIdUsuario() {
        return idUsuario;
    }

    public void setIdUsuario(int idUsuario) {
        this.idUsuario = idUsuario;
    }

    public String getNombreCompleto() {
        return nombreCompleto;
    }

    public void setNombreCompleto(String nombreCompleto) {
        this.nombreCompleto = nombreCompleto;
    }

    public String getEmail() {
        return email;
    }

    public void setEmail(String email) {
        this.email = email;
    }

    public String getPassword() {
        return password;
    }

    public void setPassword(String password) {
        this.password = password;
    }

    public String getRol() {
        return rol;
    }

    public void setRol(String rol) {
        this.rol = rol;
    }

    public boolean isEstado() {
        return estado;
    }

    public void setEstado(boolean estado) {
        this.estado = estado;
    }

    @Override
    public String toString() {
        return "Usuario{" +
                "idUsuario=" + idUsuario +
                ", nombreCompleto='" + nombreCompleto + '\'' +
                ", email='" + email + '\'' +
                ", rol='" + rol + '\'' +
                ", estado=" + estado +
                '}';
    }
}

